package com.chengxin.Entity;import java.io.Serializable;import java.util.ArrayList;import java.util.List;import com.chengxin.org.json.JSONArray;import com.chengxin.org.json.JSONException;import com.chengxin.org.json.JSONObject;public class AdDomainList implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 1L;	public List< AdDomain > mList;	public WeiYuanState mState;	public PageInfo mPageInfo;	public AdDomainList(){}		public AdDomainList(String reString){		try {			JSONObject json = new JSONObject(reString);			if(!json.isNull("data")){				JSONArray array = json.getJSONArray("data");				if(array != null){					mList = new ArrayList< AdDomain >();					List< AdDomain > tempList = AdDomain.constructList(array);					if(tempList != null){						mList.addAll(tempList);					}				}			}						if(!json.isNull("state")){				mState = new WeiYuanState(json.getJSONObject("state"));			}						if(!json.isNull("pageInfo")){				mPageInfo = new PageInfo(json.getJSONObject("pageInfo"));			}		} catch (JSONException e) {			e.printStackTrace();		}	}	public int size() {		if (mList != null) 			return mList.size();		else			return 0;	}}