package com.chengxin.Entity;import java.io.Serializable;import java.util.ArrayList;import java.util.List;import com.chengxin.org.json.JSONArray;import com.chengxin.org.json.JSONException;import com.chengxin.org.json.JSONObject;public class BillList implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 1L;		public List<Bill> mBillList;	public WeiYuanState mState;	public PageInfo mPageInfo;		public BillList(){}		public BillList(String reString){		try {			JSONObject json = new JSONObject(reString);						if(!json.isNull("state")){				mState = new WeiYuanState(json.getJSONObject("state"));			}						if(!json.isNull("pageInfo")){				mPageInfo = new PageInfo(json.getJSONObject("pageInfo"));			}			if(!json.isNull("data")){				JSONArray array = json.getJSONArray("data");				if(array != null){					mBillList = new ArrayList<Bill>();					List<Bill> tempList = Bill.constructList(array);					if(tempList != null){						mBillList.addAll(tempList);					}				}			}		} catch (JSONException e) {			e.printStackTrace();		}	}		public int size() {		if (mBillList != null)		return mBillList.size();		else return 0;	}}