package com.chengxin.Entity;import java.io.Serializable;import java.util.List;public class CheckItem implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 1L;	public int id;	public String name;	public Boolean isChecked;		public CheckItem() {		super();		id = 0;		name = "";		isChecked = false;	}		public CheckItem(int id, String name) {		super();		this.id = id;		this.name = name;		this.isChecked = false;	}	public CheckItem(int id, String name, Boolean isChecked) {		super();		this.id = id;		this.name = name;		this.isChecked = isChecked;	}	public static void setCheckByIds(List<CheckItem> list, String[] category) {		if (category != null && category.length > 0) {			if (list != null && list.size() >= category.length) {				for (String id : category) {					for (CheckItem item : list) {						String mId = String.valueOf(item.id);												if (id.equals(mId)) {							item.isChecked = true;							break;						}					}				}			}		}	}}