package com.chengxin.Entity;import java.io.Serializable;import java.util.ArrayList;import java.util.List;import com.chengxin.org.json.JSONArray;import com.chengxin.org.json.JSONException;import com.chengxin.org.json.JSONObject;public class FinancierList implements Serializable {        /**     *     */    private static final long serialVersionUID = 1L;        public List<Financier> mFinancierList;    public WeiYuanState mState;    public PageInfo mPageInfo;        public FinancierList(){}        public FinancierList(String reString){        try {            JSONObject json = new JSONObject(reString);                        if(!json.isNull("state")){                mState = new WeiYuanState(json.getJSONObject("state"));            }                        if(!json.isNull("pageInfo")){                mPageInfo = new PageInfo(json.getJSONObject("pageInfo"));            }                        if(!json.isNull("data")){                JSONArray array = json.getJSONArray("data");                                if(array != null){                    mFinancierList = new ArrayList<Financier>();                    List<Financier> tempList = Financier.constructList(array);                                        if(tempList != null){                        mFinancierList.addAll(tempList);                    }                }            }        } catch (JSONException e) {            e.printStackTrace();        }    }        public int size() {        if (mFinancierList != null)		return mFinancierList.size();        else return 0;    }    }