package com.chengxin.Entity;import java.io.Serializable;import java.util.ArrayList;import java.util.List;import com.chengxin.org.json.JSONArray;import com.chengxin.org.json.JSONException;import com.chengxin.org.json.JSONObject;public class MerchantGoodsList  implements Serializable{        /**     *     */    private static final long serialVersionUID = 1L;        public List<MerchantGoods> mList;    public WeiYuanState mState;    public PageInfo mPageInfo;        public MerchantGoodsList(){}        public MerchantGoodsList(String reString){        try {            JSONObject json = new JSONObject(reString);                        if(!json.isNull("state")){                mState = new WeiYuanState(json.getJSONObject("state"));            }                        if(!json.isNull("pageInfo")){                mPageInfo = new PageInfo(json.getJSONObject("pageInfo"));            }                        if(!json.isNull("data")){                JSONArray array = json.getJSONArray("data");                                if(array != null){                    mList = new ArrayList<MerchantGoods>();                    List<MerchantGoods> tempList = MerchantGoods.constructList(array);                                        if(tempList != null){                    	mList.addAll(tempList);                    }                }            }        } catch (JSONException e) {            e.printStackTrace();        }    }        public int size() {        if (mList != null)		return mList.size();        else return 0;    }}