package com.chengxin.Entity;import java.io.Serializable;import java.util.ArrayList;import java.util.List;import com.chengxin.org.json.JSONArray;import com.chengxin.org.json.JSONException;import com.chengxin.org.json.JSONObject;public class MerchantList implements Serializable {        /**     *     */    private static final long serialVersionUID = 1L;        public List<MerchantInfo> mMerchantList;    public WeiYuanState mState;    public PageInfo mPageInfo;        public MerchantList(){}        public MerchantList(String reString){        try {            JSONObject json = new JSONObject(reString);                        if(!json.isNull("state")){                mState = new WeiYuanState(json.getJSONObject("state"));            }                        if(!json.isNull("pageInfo")){                mPageInfo = new PageInfo(json.getJSONObject("pageInfo"));            }                        if(!json.isNull("data")){                JSONArray array = json.getJSONArray("data");                                if(array != null){                	mMerchantList = new ArrayList< MerchantInfo >();                    List<MerchantInfo> tempList = MerchantInfo.constructList(array);                                        if(tempList != null){                    	mMerchantList.addAll(tempList);                    }                }            }        } catch (JSONException e) {            e.printStackTrace();        }    }        public int size() {        if (mMerchantList != null)		return mMerchantList.size();        else return 0;    }    }