package com.chengxin.Entity;import java.io.Serializable;import java.util.ArrayList;import java.util.List;import com.chengxin.org.json.JSONArray;import com.chengxin.org.json.JSONException;import com.chengxin.org.json.JSONObject;public class ShopAreaList implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 1L;	public static List<String> mList;	public WeiYuanState mState;	public PageInfo mPageInfo;	public static boolean hasData() {		return (mList != null && mList.size() > 0);	}		public ShopAreaList(){		mList = new ArrayList<String>();	}		/**	 * @param reString	 */	public ShopAreaList(String reString){		try {			mList = new ArrayList<String>();			JSONObject json = new JSONObject(reString);						if(!json.isNull("data")){				JSONArray array = json.getJSONArray("data");				if(array != null){					for (int i = 0; i < array.length(); i++) {						mList.add(array.getJSONObject(i).getString("city"));					}				}			}						if(!json.isNull("state")){				mState = new WeiYuanState(json.getJSONObject("state"));			}						if(!json.isNull("pageInfo")){				mPageInfo = new PageInfo(json.getJSONObject("pageInfo"));			}		} catch (JSONException e) {			e.printStackTrace();		}	}	public int size() {		return mList.size();	}		public static List<PopItem> getPopMenuList() {		List<PopItem> popList = new ArrayList<PopItem>();		PopItem pop = new PopItem(0, "所有地区");		popList.add(pop);				for (int i = 0; i < mList.size(); i++) {			String item = mList.get(i);			pop = new PopItem(i + 1, item);			popList.add(pop);		}				return popList;	}}