package com.chengxin.Entity;import java.io.Serializable;public class ShopInfoItem implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 564708721532958690L;	public String title;	public String content;	public String action;	public boolean enabled;		public Object obj;    	public ShopInfoItem() {        title = null;        content = null;        action = null;        enabled = false;    }    	public ShopInfoItem(String t, String c, String a, boolean e) {        title = t;        content = c;        action = a;        enabled = e;    }		}