package com.chengxin.Entity;import java.io.Serializable;import java.util.ArrayList;import java.util.List;import com.chengxin.org.json.JSONArray;import com.chengxin.org.json.JSONException;import com.chengxin.org.json.JSONObject;public class ShopServiceList implements Serializable {	private static final long serialVersionUID = 6613804187497728155L;	public List<ShopService> mList;	public WeiYuanState mState;	public PageInfo mPageInfo;	public ShopServiceList(){		mList = new ArrayList<ShopService>();	}		/**	 * @param reString	 * @param type	 */	public ShopServiceList(String reString){		try {			mList = new ArrayList<ShopService>();			JSONObject json = new JSONObject(reString);						if(!json.isNull("data")){				JSONArray array = json.getJSONArray("data");				if(array != null){					for (int i = 0; i < array.length(); i++) {						mList.add(new ShopService(array.getJSONObject(i)));					}				}			}						if(!json.isNull("state")){				mState = new WeiYuanState(json.getJSONObject("state"));			}						if(!json.isNull("pageInfo")){				mPageInfo = new PageInfo(json.getJSONObject("pageInfo"));			}		} catch (JSONException e) {			e.printStackTrace();		}	}	public int size() {		return mList.size();	}}