package com.chengxin.Entity;import java.util.ArrayList;import java.util.List;public class StatusMenu {    /**     *	@Copyright © 2015 tcy@dreamisland. All rights reserved.     *	订单状态     *  @param status    1:等待发货     *                   2:已发货     *                   3:未付款     *                   4:退货中     *                   5:已退单     *                   6:已完成     *                   7:已退货     *                   8:结款中     *                   9:已结款     */	private static List< String > list = new ArrayList<String>();		static void initList() {		list.add("所有状态");		list.add("等待发货");		list.add("已发货");		list.add("未付款");		list.add("退货中");		list.add("已退单");		list.add("已完成");		list.add("已退货");		list.add("结款中");		list.add("已结款");	}	public static List<PopItem> getPopMenuList() {		if (list.size() == 0) initList();				List<PopItem> popList = new ArrayList<PopItem>();		for (int i = 0; i < list.size(); i ++) {			popList.add(new PopItem(i, list.get(i)));		}				return popList;	}	public static String getStatusStringById(String status) {		if (list.size() == 0) initList();		int id = Integer.valueOf(status).intValue();				if (id >= 0 && id < list.size()) {			return list.get(id);		}				return null;	}}