package com.chengxin;import android.os.Bundle;import android.view.View;import com.chengxin.BaseActivity;import com.chengxin.R;public class PublicNoActivity extends BaseActivity {	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		mContext = this;		setContentView(R.layout.publicno_activity);		initComponent();	}	private void initComponent() {		setTitleContent(R.drawable.back_btn, 0, R.string.apply_exhibition);		mLeftBtn.setOnClickListener(this);	}	@Override	public void onClick(View v) {		super.onClick(v);				switch (v.getId()) {		case R.id.left_btn:			this.finish();			break;		default:			break;		}	}}