package com.chengxin.adapter;import java.util.Date;import java.util.List;import com.chengxin.R;import com.chengxin.Entity.Bill;import com.chengxin.global.FeatureFunction;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;public class BillAdapter extends BaseAdapter {	private List<Bill> mList;	private Context mContext;	private LayoutInflater mInflater;	public BillAdapter(Context context, List<Bill> list) {		super();		this.mList = list;		this.mContext = context;		mInflater = LayoutInflater.from(context); 	}	@Override	public int getCount() {		return mList.size();	}	@Override	public Object getItem(int position) {		return mList.get(position);	}	@Override	public long getItemId(int position) {		return Integer.valueOf(mList.get(position).id).longValue();	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		ViewHolder holder;		if (convertView == null) {			convertView = mInflater.inflate(R.layout.shop_bill_item, null);			holder = new ViewHolder();			holder.title = (TextView)convertView.findViewById(R.id.title);			holder.subtitle = (TextView)convertView.findViewById(R.id.subtitle);			holder.rightText = (TextView)convertView.findViewById(R.id.right_text);			convertView.setTag(holder);		}else{			holder = (ViewHolder) convertView.getTag();		}		Bill item = mList.get(position);				String title = null;		String date = null;		String price = null;				if (item.type == 1) {			title = item.name;			date = String.format("每月%d日提醒", item.repayment);			price = String.format("￥%.2f", item.price);		} else if (item.type == 2) {			title = item.bank;			Date createDate = new Date(item.repayment * 1000);			date = FeatureFunction.getDateString(mContext, createDate, 4) + "到期";			price = "信用卡还款";		}				if (title != null && !title.equals("") && !title.equals("null")) {			holder.title.setText(title);		}		if (date != null && !date.equals("")) {			holder.subtitle.setText(date);		}		if (price != null && !price.equals("")) {			holder.rightText.setText(price);		}		return convertView;	}		public static class ViewHolder {		public TextView title;		public TextView subtitle; 		public TextView rightText;		@Override		public int hashCode() {			return this.title.hashCode() + this.subtitle.hashCode()					+ this.rightText.hashCode();		}	}}