package com.chengxin.adapter;import java.util.Date;import java.util.List;import com.chengxin.R;import com.chengxin.Entity.Bill;import com.chengxin.Entity.BillRepay;import com.chengxin.adapter.BillAdapter.ViewHolder;import com.chengxin.global.FeatureFunction;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.CheckBox;import android.widget.CompoundButton;import android.widget.ImageView;import android.widget.LinearLayout;import android.widget.TextView;import android.widget.CompoundButton.OnCheckedChangeListener;public class BillRepayAdapter extends BaseAdapter {	private List<BillRepay> mList;	private Context mContext;	private LayoutInflater mInflater;	public BillRepayAdapter(Context context, List<BillRepay> list) {		super();		this.mList = list;		this.mContext = context;		mInflater = LayoutInflater.from(context); 	}	@Override	public int getCount() {		return mList.size();	}	@Override	public Object getItem(int position) {		return mList.get(position);	}	@Override	public long getItemId(int position) {		return Integer.valueOf(mList.get(position).id).longValue();	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		ViewHolder holder;		if (convertView == null || ((ViewHolder) convertView.getTag()).position != position) {			convertView = mInflater.inflate(R.layout.bill_repay_item, null);			holder = new ViewHolder();			holder.position = position;						holder.repayDate 			= (TextView)convertView.findViewById(R.id.repay_date);			holder.repayMoney 			= (TextView)convertView.findViewById(R.id.repay_money);			convertView.setTag(holder);		}else{			holder = (ViewHolder) convertView.getTag();		}		BillRepay item = mList.get(position);				Date date = new Date(item.createTime * 1000);				if (date != null) {			holder.repayDate.setText(FeatureFunction.getDateString(mContext, date, 101));		}		holder.repayMoney.setText(String.format("ï¿¥%.2f", item.repayMoney));				return convertView;	}		public static class ViewHolder {		public TextView 	repayDate;		public TextView 	repayMoney;		public int position;				@Override		public int hashCode() {			return repayDate.hashCode() + repayMoney.hashCode();		}	}}