package com.chengxin.adapter;import java.util.List;import com.chengxin.R;import com.chengxin.Entity.Demand;import com.chengxin.Entity.Goods;import com.chengxin.adapter.OrderGoodsAdapter.ViewHolder;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;public class DemandAdapter extends BaseAdapter {	private List< Demand > list;	private LayoutInflater mInflater;		public DemandAdapter(Context context, List<Demand> list) {		super();		this.list = list;		mInflater = LayoutInflater.from(context); 	}	@Override	public int getCount() {		return list.size();	}	@Override	public Object getItem(int position) {		return list.get(position);	}	@Override	public long getItemId(int position) {		return Integer.valueOf(list.get(position).uid).longValue();	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		ViewHolder holder;		if (convertView == null) {			convertView = mInflater.inflate(R.layout.services_require_item, null);			holder = new ViewHolder();			holder.content   = (TextView)convertView.findViewById(R.id.content);			holder.distance  = (TextView)convertView.findViewById(R.id.distance);			convertView.setTag(holder);		}else{			holder = (ViewHolder) convertView.getTag();		}		Demand item = list.get(position);				holder.content.setText(item.content);		holder.distance.setText(String.format("距离%n%d公里", (int)(item.distance / 1000)).toString());		return convertView;	}	public final static class ViewHolder {		public TextView content;		public TextView distance;		@Override		public int hashCode() {			return this.content.hashCode() + this.distance.hashCode();		}	}}