package com.chengxin.adapter;import java.util.List;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.TextView;import com.chengxin.R;import com.chengxin.Entity.FinancingGoods;import com.chengxin.global.ImageLoader;public class FinancingGoodsAdapter extends BaseAdapter {	private List<FinancingGoods> mList;	private Context mContext;	private LayoutInflater mInflater;	private ImageLoader mImageLoader;	public FinancingGoodsAdapter(Context context, List<FinancingGoods> list) {		super();		this.mList = list;		this.mContext = context;		mInflater = LayoutInflater.from(context);		mImageLoader = new ImageLoader();	}	@Override	public int getCount() {		return mList.size();	}	@Override	public Object getItem(int position) {		return mList.get(position);	}	@Override	public long getItemId(int position) {		return Integer.valueOf(mList.get(position).id).longValue();	}	@Override	public View getView(final int position, View convertView, ViewGroup parent) {		ViewHolder holder;		if (convertView == null || ((ViewHolder)convertView.getTag()).position != position) {			convertView = mInflater.inflate(R.layout.financing_product_item, null);			holder = new ViewHolder();			holder.position = position;						holder.imageIcon = (ImageView)convertView.findViewById(R.id.image_icon);						holder.textName = (TextView)convertView.findViewById(R.id.text_name);			holder.textLimit = (TextView)convertView.findViewById(R.id.text_limit);			holder.textRepay = (TextView)convertView.findViewById(R.id.text_repay);			holder.textLoan = (TextView)convertView.findViewById(R.id.text_loan);						convertView.setTag(holder);		}else{			holder = (ViewHolder) convertView.getTag();		}		final FinancingGoods item = mList.get(position);		if (item.headsmall != null && !item.headsmall.equals("") && !item.headsmall.equals("null")) {			mImageLoader.getBitmap(mContext, holder.imageIcon, null, item.headsmall, 0, false, false, false);		}				if (item.name != null && !item.name.equals("") && !item.name.equals("null")) {			holder.textName.setText(item.name);		}		if (item.loanAmount != null && !item.loanAmount.equals("") && !item.loanAmount.equals("null")) {			holder.textLimit.setText(item.getLoanAmountString());		}				if (item.monthRepay > 0) {			holder.textRepay.setText(item.getMonthRepayString());		}		if (item.loanTime >= 0) {			holder.textLoan.setText(item.getLoanTimeString());		}				return convertView;	}		public static class ViewHolder {		public int position;				public ImageView imageIcon;		public TextView textName;		public TextView textLimit; 		public TextView textRepay;		public TextView textLoan;				@Override		public int hashCode() {			return imageIcon.hashCode() + textName.hashCode()					+ textLimit.hashCode() + textRepay.hashCode()					+ textLoan.hashCode();		}	}}