package com.chengxin.adapter;import java.util.List;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.View.OnClickListener;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.TextView;import com.chengxin.R;import com.chengxin.Entity.FinancingGoods;import com.chengxin.global.ImageLoader;public class FinancingGoodsAdapter extends BaseAdapter {	public interface OnFinancingGoodsClickListener {		public void onClick(View v, int Position, int uid);	}	public int type = 0;		private List<FinancingGoods> mList;	private Context mContext;	private LayoutInflater mInflater;	private ImageLoader mImageLoader;	private OnFinancingGoodsClickListener mOnClickListener = null;	public void setOnFinancingGoodsClickListener(OnFinancingGoodsClickListener l) {		mOnClickListener = l;	}		public FinancingGoodsAdapter(Context context, List<FinancingGoods> list) {		super();		this.mList = list;		this.mContext = context;		mInflater = LayoutInflater.from(context);		mImageLoader = new ImageLoader();	}	@Override	public int getCount() {		return mList.size();	}	@Override	public Object getItem(int position) {		return mList.get(position);	}	@Override	public long getItemId(int position) {		return Integer.valueOf(mList.get(position).id).longValue();	}	@Override	public View getView(final int position, View convertView, ViewGroup parent) {		ViewHolder holder;		if (convertView == null) {			convertView = mInflater.inflate(R.layout.financing_goods_item, null);			holder = new ViewHolder();			holder.icon = (ImageView)convertView.findViewById(R.id.head_icon);						holder.sumary = (TextView)convertView.findViewById(R.id.goods_sumary);			holder.feature = (TextView)convertView.findViewById(R.id.features);			holder.material = (TextView)convertView.findViewById(R.id.material);			holder.btn = (TextView)convertView.findViewById(R.id.btn_asking);			if (type == 1) {				holder.icon.setVisibility(View.GONE);				holder.btn.setText("删除");			}			convertView.setTag(holder);		}else{			holder = (ViewHolder) convertView.getTag();		}		final FinancingGoods item = mList.get(position);		if (item.headsmall != null && !item.headsmall.equals("") && !item.headsmall.equals("null")) {			mImageLoader.getBitmap(mContext, holder.icon, null, item.headsmall, 0, false, false, false);		}				String sumary = "";				if (item.name != null && !item.name.equals("") && !item.name.equals("null")) {			sumary = sumary + item.name;		}				if (item.type != null && !item.type.equals("") && !item.type.equals("null")) {			sumary = sumary + ":" + item.type;		}				holder.sumary.setText(sumary);		if (item.features != null && !item.features.equals("") && !item.features.equals("null")) {			holder.feature.setText("特征: " + item.features);		}				if (item.material != null && !item.material.equals("") && !item.material.equals("null")) {			holder.material.setText("需要材料: " + item.material);		}				holder.btn.setOnClickListener(new OnClickListener() {						@Override			public void onClick(View v) {				if (mOnClickListener != null) {					mOnClickListener.onClick(v, position, Integer.valueOf(item.uid));				}			}		});				return convertView;	}		public static class ViewHolder {		public ImageView icon;		public TextView sumary;		public TextView feature; 		public TextView material;		public TextView btn;		@Override		public int hashCode() {			return this.icon.hashCode() + this.sumary.hashCode()					+ this.feature.hashCode() + this.material.hashCode()					+ this.btn.hashCode();		}	}}