package com.chengxin.adapter;import java.util.ArrayList;import java.util.List;import com.chengxin.R;import com.chengxin.Entity.GridViewMenuItem;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.ListAdapter;import android.widget.TextView;public class GridMenuAdapter extends BaseAdapter implements ListAdapter {	private List < GridViewMenuItem > list;	private LayoutInflater mInflater;		public GridMenuAdapter(Context context, List<GridViewMenuItem> list) {		super();		this.list = list;		mInflater = LayoutInflater.from(context); 	}	@Override	public int getCount() {		return list.size();	}	@Override	public Object getItem(int position) {		return list.get(position);	}	@Override	public long getItemId(int position) {		return list.get(position).id;	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		ViewHolder holder;				if(convertView == null || ((ViewHolder)convertView.getTag()).mTag != position){			holder = new ViewHolder();			convertView = mInflater.inflate(R.layout.gridview_menu_item, null);			holder.mTag = position;			holder.mMenuText = (TextView)convertView.findViewById(R.id.menu_text);			holder.mMenuIcon = (ImageView)convertView.findViewById(R.id.menu_icon);			convertView.setTag(holder);		} else {			holder = (ViewHolder) convertView.getTag();		}				GridViewMenuItem item = list.get(position);				if (item.resource_id > 0) {			holder.mMenuIcon.setImageResource(item.resource_id);		}				holder.mMenuText.setText(item.menu_name);				return convertView;	}	static class ViewHolder	{		int mTag;		TextView mMenuText;		ImageView mMenuIcon;	}}