package com.chengxin.adapter;import java.util.List;import com.chengxin.R;import com.chengxin.Entity.Goods;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;public class OrderGoodsAdapter extends BaseAdapter {	private List<Goods> list;	private LayoutInflater mInflater;		public OrderGoodsAdapter(Context mContext, List<Goods> list) {		super();		this.list = list;		mInflater = LayoutInflater.from(mContext); 	}	@Override	public int getCount() {		return list.size();	}	@Override	public Object getItem(int position) {		return list.get(position);	}	@Override	public long getItemId(int position) {		return Integer.valueOf(list.get(position).id).longValue();	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		ViewHolder holder;		if (convertView == null) {			convertView = mInflater.inflate(R.layout.order_goods_item, null);			holder = new ViewHolder();			holder.mGoodsName   = (TextView)convertView.findViewById(R.id.text_name);			holder.mGoodsPrice  = (TextView)convertView.findViewById(R.id.text_price);			holder.mGoodsCount 	= (TextView)convertView.findViewById(R.id.text_count);			holder.mGoodsSum = (TextView)convertView.findViewById(R.id.text_sum);			convertView.setTag(holder);		}else{			holder = (ViewHolder) convertView.getTag();		}		Goods item = list.get(position);				holder.mGoodsName.setText(item.name);		holder.mGoodsPrice.setText(String.valueOf(item.price).toString());		holder.mGoodsCount.setText(String.valueOf(item.count).toString());		holder.mGoodsSum.setText(String.valueOf(item.count * item.price).toString());		return convertView;	}	public final static class ViewHolder {		public TextView mGoodsSum;		public TextView mGoodsCount;		public TextView mGoodsPrice;		public TextView mGoodsName;		@Override		public int hashCode() {			return this.mGoodsSum.hashCode() + this.mGoodsCount.hashCode()					+ this.mGoodsPrice.hashCode() + this.mGoodsName.hashCode();		}	}}