package com.chengxin.adapter;import java.util.Date;import java.util.List;import com.chengxin.R;import com.chengxin.Entity.Order;import com.chengxin.Entity.StatusMenu;import com.chengxin.global.FeatureFunction;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;public class ShopOrderAdapter extends BaseAdapter {	private Context mContext;	private List<Order> list;	private LayoutInflater mInflater;		public ShopOrderAdapter(Context context, List<Order> l) {		super();		mContext = context;		list = l;		mInflater = LayoutInflater.from(mContext); 	}	@Override	public int getCount() {		return list.size();	}	@Override	public Object getItem(int position) {		return list.get(position);	}	@Override	public long getItemId(int position) {		return Integer.valueOf(list.get(position).id).longValue();	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		ViewHolder holder;		if (convertView == null) {			convertView = mInflater.inflate(R.layout.shop_order_item, null);			holder = new ViewHolder();			holder.mSerial      = (TextView)convertView.findViewById(R.id.lbl_serial);			holder.mOrderName   = (TextView)convertView.findViewById(R.id.order_number);			holder.mOrderBrief  = (TextView)convertView.findViewById(R.id.order_brief);			holder.mOrderDate 	= (TextView)convertView.findViewById(R.id.order_date);			holder.mOrderStatus = (TextView)convertView.findViewById(R.id.order_status);			convertView.setTag(holder);		}else{			holder = (ViewHolder) convertView.getTag();		}		final Order item = list.get(position);				holder.mSerial.setText(String.valueOf(position + 1).toString());				if (item.ordersn != null && !item.ordersn.equals("") && !item.ordersn.equals("null"))			holder.mOrderName.setText(String.format("订单号: %s", item.ordersn));		else			holder.mOrderName.setText("订单号:");							holder.mOrderBrief.setText(String.format("总金额: %.2f  共计 %d 件", item.totalPrice, item.totalCount));		Date createDate = new Date(Long.parseLong(item.createtime) * 1000);		holder.mOrderDate.setText(String.format("下单时间: %s", FeatureFunction.getDateString(mContext, createDate, 1)));		holder.mOrderStatus.setText(StatusMenu.getStatusStringById(item.status));		return convertView;	}	public final static class ViewHolder {		public TextView mSerial;		public TextView mOrderName;		public TextView mOrderBrief; 		public TextView mOrderDate;		public TextView mOrderStatus;		@Override		public int hashCode() {			return this.mSerial.hashCode() + this.mOrderName.hashCode()					+ this.mOrderBrief.hashCode() + this.mOrderDate.hashCode()					+ this.mOrderStatus.hashCode();		}	}}