package com.chengxin.financial;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.EditText;import android.widget.LinearLayout;import android.widget.Toast;import com.chengxin.BaseActivity;import com.chengxin.R;import com.chengxin.Entity.Bill;import com.chengxin.dialog.CPAlert;import com.chengxin.dialog.CPAlert.OnAlertDaySelectId;public class BillInfoActivity extends BaseActivity {	private Bill mBill = null;		private LinearLayout mLabelLayout;	private EditText mEditName;	private EditText mEditMechanism;	private EditText mEditDate;	private EditText mEditMoney;	private EditText mEditNumber;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		mContext = this;		setContentView(R.layout.edit_bill_activity);				initComponent();		getData();	}	private void initComponent() {		if (getIntent().hasExtra("data")) {			mBill = (Bill)getIntent().getExtras().get("data");		}		setTitleContent(R.drawable.back_btn, R.drawable.ok, R.string.bill_info_title);		mLeftBtn.setOnClickListener(this);		mRightBtn.setOnClickListener(this);		mLabelLayout = (LinearLayout)findViewById(R.id.layout_label);		mEditName = (EditText)findViewById(R.id.edt_name);		mEditMechanism = (EditText)findViewById(R.id.edt_mechanism);				mEditDate = (EditText)findViewById(R.id.edt_date);		mEditDate.setFocusable(false);		mEditDate.setFocusableInTouchMode(false);		mEditDate.setOnClickListener(this);				mEditMoney = (EditText)findViewById(R.id.edt_money);		mEditNumber = (EditText)findViewById(R.id.edt_number);				if (mBill != null && mBill.id > 0) {			mLabelLayout.setVisibility(View.VISIBLE);		} else {			mBill = new Bill(1);		}	}	private void getData() {		if (mBill != null && mBill.id > 0) {			mEditName.setText(mBill.name);			mEditMechanism.setText(mBill.mechanism);			mEditDate.setText(String.valueOf(mBill.repayment));			mEditMoney.setText(String.format("%.2f", mBill.price));			mEditNumber.setText(String.valueOf(mBill.number));		}	}	@Override	public void onClick(View v) {		super.onClick(v);				switch (v.getId()) {		case R.id.left_btn:			this.finish();			break;		case R.id.right_btn:			updateData();			break;		case R.id.edt_date:			int day = 1;			String strDay = mEditDate.getText().toString(); 			if (strDay != null && !strDay.equals("")) {				day = Integer.parseInt(strDay);			}						CPAlert.showAlert(					mContext,					"选择还款日",					day,					new OnAlertDaySelectId() {												@Override						public void onOkClick(int whichButton, int day) {							mEditDate.setText(String.valueOf(day));						}					});			break;		default:			break;		}	}	private void updateData() {		if (mBill != null) {			mBill.name = mEditName.getText().toString();			mBill.mechanism = mEditMechanism.getText().toString();			mBill.repayment = Long.parseLong(mEditDate.getText().toString());			mBill.price = Double.parseDouble(mEditMoney.getText().toString());			mBill.number = Integer.parseInt(mEditNumber.getText().toString());						String hint = null;						if (mBill.name == null || mBill.name.equals("")) {				hint = "请输入还款名称";			} else if (mBill.mechanism == null || mBill.mechanism.equals("")) {				hint = "请输入还款机构";			} else if (mBill.repayment < 1 || mBill.repayment > 31) {				hint = "请正确输入每月的提醒日期";			} else if (mBill.price < 1) {				hint = "请输入到期的还款金额";			}						if (hint != null && hint.length() > 0) {				Toast.makeText(mContext, hint, Toast.LENGTH_LONG).show();;			} else {				submit();			}		}	}	private void submit() {		Intent intent = new Intent();		intent.putExtra("data", mBill);		setResult(RESULT_OK, intent);		this.finish();	}}