package com.chengxin.fragment;import com.chengxin.R;import android.content.Context;import android.os.Bundle;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;public class ExpectFragment extends Fragment {	private Context mParentContext;	private View mView;	private TextView mPrompt;	@Override	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		mParentContext = (Context) ExpectFragment.this.getActivity();	}		@Override	public void onActivityCreated(Bundle savedInstanceState) {		super.onActivityCreated(savedInstanceState);		mPrompt = (TextView)mView.findViewById(R.id.text_prompt);	}	@Override	public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {		mView = inflater.inflate(R.layout.fragment_expect, container, false);		return mView;	}}