package com.chengxin.fragment;import android.annotation.SuppressLint;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;import android.content.IntentFilter;import android.graphics.Color;import android.os.Bundle;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.View.OnClickListener;import android.view.ViewGroup;import android.widget.Button;import android.widget.LinearLayout;import android.widget.RelativeLayout;import android.widget.TextView;import com.chengxin.R;import com.chengxin.ShoppingCartActivity;import com.chengxin.global.GlobalParam;import com.chengxin.global.ScreenUtils;import com.chengxin.global.WeiYuanCommon;@SuppressLint("NewApi")public class ShopCityFragment extends Fragment implements OnClickListener {	private Context mContext;	private View mView;	private LinearLayout mLayoutGoods;	private Button mButtonGoods;	private Button mButtonShop;	private TextView mButtonNotice;	private LinearLayout mLayoutShop;	private RelativeLayout mBuyBtn;	private TextView mGoodsCountTextView;	@Override	public void onActivityCreated(Bundle savedInstanceState) {		super.onActivityCreated(savedInstanceState);		IntentFilter filter = new IntentFilter();		filter.addAction(GlobalParam.ACTION_REFRESH_MERCHANT_GOODS_COUNT);				mContext.registerReceiver(mRefreshReceiver, filter);		mBuyBtn = (RelativeLayout)mView.findViewById(R.id.buy_btn);		mBuyBtn.setOnClickListener(this);		mGoodsCountTextView = (TextView)mView.findViewById(R.id.shpping_number);		mGoodsCountTextView.setText(WeiYuanCommon.getGoodsCount()+"");		mLayoutGoods = (LinearLayout)mView.findViewById(R.id.goods_layout);		mLayoutShop = (LinearLayout)mView.findViewById(R.id.shop_layout);				mButtonGoods = (Button)mView.findViewById(R.id.btn_goods);		mButtonShop = (Button)mView.findViewById(R.id.btn_shop);		mButtonNotice = (TextView)mView.findViewById(R.id.btn_notice);		mButtonGoods.setOnClickListener(this);		mButtonShop.setOnClickListener(this);		mButtonNotice.setOnClickListener(this);	}	@Override	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		mContext = ShopCityFragment.this.getActivity();	}	@Override	public View onCreateView(LayoutInflater inflater, ViewGroup container,			Bundle savedInstanceState) {		mView= inflater.inflate(R.layout.shop_city_fragment, container, false);		return mView;		}	@Override	public void onDestroy() {		super.onDestroy();		System.gc();		if(mRefreshReceiver != null){			mContext.unregisterReceiver(mRefreshReceiver);		}	}	@Override	public void onClick(View v) {		switch (v.getId()) {		case R.id.buy_btn:			Intent cartIntent = new Intent();			cartIntent.setClass(mContext, ShoppingCartActivity.class);			startActivity(cartIntent);			break;		case R.id.btn_goods:			mLayoutGoods.setVisibility(View.VISIBLE);			mLayoutShop.setVisibility(View.GONE);			mButtonGoods.setBackgroundResource(R.color.application_blue);			mButtonShop.setBackgroundResource(R.color.application_gray_black);			mButtonGoods.setTextColor(Color.parseColor("#ffffff"));			mButtonShop.setTextColor(Color.parseColor("#010101"));			break;		case R.id.btn_shop:			mLayoutShop.setVisibility(View.VISIBLE);			mLayoutGoods.setVisibility(View.GONE);			mButtonShop.setBackgroundResource(R.color.application_blue);			mButtonGoods.setBackgroundResource(R.color.application_gray_black);			mButtonShop.setTextColor(Color.parseColor("#ffffff"));			mButtonGoods.setTextColor(Color.parseColor("#010101"));			break;		case R.id.btn_notice:			break;		default:			break;		}	}		private BroadcastReceiver mRefreshReceiver = new BroadcastReceiver() {		@Override		public void onReceive(Context context, Intent intent) {			if(intent == null || 					(intent.getAction() == null || intent.getAction().equals(""))){				return;			}			String action = intent.getAction();			if(action.equals(GlobalParam.ACTION_REFRESH_MERCHANT_GOODS_COUNT)){				mGoodsCountTextView.setText(WeiYuanCommon.getGoodsCount()+"");			}		}	};}