package com.chengxin.profile.shopping;import android.os.Bundle;import android.view.View;import com.chengxin.BaseActivity;import com.chengxin.R;public class ShopOrderLogisticsTraceActivity extends BaseActivity {	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		mContext = this;		setContentView(R.layout.logistics_trace_info);		setTitleContent(R.drawable.back_btn, 0, R.string.logistics_info);		mLeftBtn.setOnClickListener(this);	}	@Override	public void onClick(View v) {		super.onClick(v);				switch (v.getId()) {		case R.id.left_btn:			this.finish();			break;		}	}}