package com.chengxin.profile.shopping;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.EditText;import android.widget.TextView;import com.chengxin.BaseActivity;import com.chengxin.R;public class ShopOrderRetreatActivity extends BaseActivity {	private EditText mEditReason;	private int mOpenMode;	private TextView mLabelTitle;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		mContext = this;		setContentView(R.layout.order_retreat_activity);				mOpenMode = getIntent().getIntExtra("openMode", 0);		mLabelTitle = (TextView)findViewById(R.id.lbl_title);		mEditReason = (EditText)findViewById(R.id.edit_reason);		if (mOpenMode == 0) {			setTitleContent(R.drawable.back_btn, R.drawable.ok_btn, R.string.shop_order_retreat);		} else {			setTitleContent(R.drawable.back_btn, R.drawable.ok_btn, R.string.shop_order_appeal);			mLabelTitle.setText("申诉原因");			mEditReason.setHint("请填写申诉原因");		}		mLeftBtn.setOnClickListener(this);        mRightBtn.setOnClickListener(this);			}	@Override	public void onClick(View v) {		super.onClick(v);		switch (v.getId()) {		case R.id.left_btn:			this.finish();			break;		case R.id.right_btn:			String reason = mEditReason.getText().toString();						if (reason == null || reason.equals("")) {				return;			}						Intent intent = new Intent();			intent.putExtra("reason", reason);			setResult(RESULT_OK, intent);			this.finish();			break;		}	}}