package com.chengxin.profile.shopping;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.RelativeLayout;import com.chengxin.BaseActivity;import com.chengxin.R;public class ShoppingManagerActivity extends BaseActivity {	private RelativeLayout mShopInfoLayout;	private RelativeLayout 	mShopGoodsLayout;	private RelativeLayout 	mShopShelfLayout;	private RelativeLayout 	mShopOrderLayout;	private RelativeLayout 	mShopAccountLayout;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		mContext = this;		setContentView(R.layout.shop_manager);				initComponent();	}	private void initComponent() {		setTitleContent(R.drawable.back_btn, 0, R.string.shop_manager);		mLeftBtn.setOnClickListener(this);				mShopInfoLayout = (RelativeLayout)findViewById(R.id.layout_shop_info);		mShopInfoLayout.setOnClickListener(this);				mShopGoodsLayout = (RelativeLayout)findViewById(R.id.layout_shop_goods);		mShopGoodsLayout.setOnClickListener(this);		mShopShelfLayout = (RelativeLayout)findViewById(R.id.layout_shop_shelf);		mShopShelfLayout.setOnClickListener(this);				mShopOrderLayout = (RelativeLayout)findViewById(R.id.layout_shop_order);		mShopOrderLayout.setOnClickListener(this);				mShopAccountLayout = (RelativeLayout)findViewById(R.id.layout_shop_account);		mShopAccountLayout.setOnClickListener(this);	}	@Override	public void onClick(View v) {		super.onClick(v);				switch (v.getId()) {		case R.id.left_btn:			this.finish();			break;		case R.id.layout_shop_info:			Intent shopIntent = new Intent();			shopIntent.setClass(mContext, ShopInfoActivity.class);			startActivity(shopIntent);			break;		case R.id.layout_shop_goods:			this.finish();			break;		case R.id.layout_shop_shelf:			this.finish();			break;		case R.id.layout_shop_order:			this.finish();			break;		case R.id.layout_shop_account:			this.finish();			break;		default:			break;		}	}		}